import json
import locale
import requests

def get_amap_regeo(location, api_key, extensions="all", radius=1000):
    url = "https://restapi.amap.com/v3/geocode/regeo"
    params = {
        'key': api_key,
        'location': location,
        'extensions': extensions,
        'radius': str(radius)
    }
    response = requests.get(url, params=params)
    if response.status_code == 200:
        return response.json()
    else:
        return None

class AmapRegeoTool:
    @classmethod
    def INPUT_TYPES(cls):
        return {
            "required": {
                "location": ("STRING", {"default": "116.310003,39.991957"}),  # 默认位置坐标
                "api_key": ("STRING", {"default": "你的高德API密钥"}),
                "extensions": ("STRING", {"default": "all"}),
                "radius": ("INTEGER", {"default": 1000}),
                "is_enable": ("BOOLEAN", {"default": True}),
            }
        }

    RETURN_TYPES = ("STRING",)
    RETURN_NAMES = ("tool",)

    FUNCTION = "amap_regeo"

    CATEGORY = "大模型派对（llm_party）/工具（tools）"

    def amap_regeo(self, location, api_key, extensions="all", radius=1000, is_enable=True):
        if not is_enable:
            return (None,)
        regeo_info = get_amap_regeo(location, api_key, extensions, radius)
        if regeo_info:
            output = [
                {
                    "type": "function",
                    "function": {
                        "name": "get_amap_regeo",
                        "description": "用于根据坐标获取逆地理编码信息",
                        "parameters": {
                            "type": "object",
                            "properties": {
                                "location": {
                                    "type": "string",
                                    "description": "需要查询的经纬度坐标，例如：116.310003,39.991957",
                                    "default": str(location),
                                },
                                "api_key": {
                                    "type": "string",
                                    "description": "高德API密钥",
                                    "default": str(api_key),
                                },
                                "extensions": {
                                    "type": "string",
                                    "description": "返回结果控制",
                                    "default": str(extensions),
                                },
                                "radius": {
                                    "type": "integer",
                                    "description": "搜索半径（单位：米）",
                                    "default": radius,
                                }
                            },
                            "required": ["location", "api_key"],
                        },
                    },
                }
            ]
            out = json.dumps(output, ensure_ascii=False)
            return (out,)
        else:
            return (None,)

